mapper.select:
SELECT ID_PERSONA AS IDPERSONA FROM PERSONA
WHERE CODIGO=#{codigo}
AND CARGO='JEFE DE OFICINA'

SELECT ID_PERSONA AS IDPERSONA FROM PERSONA
WHERE CODIGO=#{codigo}
AND APELLIDOS||', '||NOMBRES=#{nombre}

mapper.update:
UPDATE PRODUCTOS_OGPL
SET
SBN = #{sbn}
WHERE ID_NUMERO = #{idnumero}
